import React, { useState } from 'react';

function LoginPage({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username && password) {
      onLogin(username);
    } else {
      alert('请输入用户名和密码');
    }
  };

  return (
    <div style={styles.container}>
      <form onSubmit={handleSubmit} style={styles.form}>
        <h2>软件工程管理平台</h2>
        <input
          type="text"
          placeholder="请输入用户名"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={styles.input}
        />
        <input
          type="password"
          placeholder="请输入密码"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={styles.input}
        />
        <button type="submit" style={styles.button}>登录</button>
      </form>
    </div>
  );
}

const styles = {
  container: { display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', background: '#f0f2f5' },
  form: { background: 'white', padding: 30, borderRadius: 10, boxShadow: '0 0 10px rgba(0,0,0,0.1)' },
  input: { width: 200, marginBottom: 15, padding: 10, fontSize: 14 },
  button: { width: '100%', padding: 10, background: '#007bff', color: 'white', border: 'none', borderRadius: 5 }
};

export default LoginPage;
