import React, { useState } from 'react';

function LoginPage({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username && password) {
      onLogin(username);
    } else {
      alert('ËØ∑ËæìÂÖ•Áî®Êà∑ÂêçÂíåÂØÜÁ†Å');
    }
  };

  return (
    <div style={styles.page}>
      <form onSubmit={handleSubmit} style={styles.card}>
        <h2 style={styles.title}>üéØ ËΩØ‰ª∂Â∑•Á®ãÁÆ°ÁêÜÂπ≥Âè∞</h2>
        <input
          type="text"
          placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={styles.input}
        />
        <input
          type="password"
          placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={styles.input}
        />
        <button type="submit" style={styles.button}>ÁôªÂΩï</button>
      </form>
    </div>
  );
}

const styles = {
  page: {
    background: 'linear-gradient(135deg, #e0eafc, #cfdef3)',
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  card: {
    background: '#fff',
    padding: '40px 30px',
    borderRadius: '12px',
    boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
    display: 'flex',
    flexDirection: 'column',
    width: '300px',
    gap: '18px',
  },
  title: {
    textAlign: 'center',
    marginBottom: '10px',
    color: '#333',
  },
  input: {
    padding: '12px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontSize: '14px',
    transition: '0.2s',
    outline: 'none',
  },
  button: {
    padding: '12px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    fontWeight: 'bold',
    fontSize: '15px',
    cursor: 'pointer',
    transition: '0.2s',
  }
};

// Ê∑ªÂä†ËÅöÁÑ¶ÊïàÊûú
styles.input[':focus'] = {
  borderColor: '#007bff',
  boxShadow: '0 0 5px rgba(0,123,255,0.4)',
};

export default LoginPage;
