import React, { useState } from 'react';

function LoginPage({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false); // ÂØÜÁ†ÅÂèØËßÅÊÄßÁä∂ÊÄÅ

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username && password) {
      onLogin(username);
    } else {
      alert('ËØ∑ËæìÂÖ•Áî®Êà∑ÂêçÂíåÂØÜÁ†Å');
    }
  };

  return (
    <div style={styles.page}>
      <form onSubmit={handleSubmit} style={styles.card}>
        <h2 style={styles.title}>üéØ ËΩØ‰ª∂Â∑•Á®ãÁÆ°ÁêÜÂπ≥Âè∞</h2>

        <input
          type="text"
          placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={styles.input}
        />

        {/* ÂØÜÁ†ÅËæìÂÖ• + ÂèØËßÅÊåâÈíÆ */}
        <div style={styles.passwordWrapper}>
          <input
            type={showPassword ? 'text' : 'password'}
            placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ ...styles.input, paddingRight: '40px' }}
          />
          <span
            onClick={() => setShowPassword(!showPassword)}
            style={styles.eyeButton}
            title={showPassword ? 'ÈöêËóèÂØÜÁ†Å' : 'ÊòæÁ§∫ÂØÜÁ†Å'}
          >
            {showPassword ? 'üôà' : 'üëÅÔ∏è'}
          </span>
        </div>

        <button type="submit" style={styles.button}>ÁôªÂΩï</button>
      </form>
    </div>
  );
}

const styles = {
  page: {
    background: 'linear-gradient(135deg, #e0eafc, #cfdef3)',
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  card: {
    background: '#fff',
    padding: '40px 30px',
    borderRadius: '12px',
    boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
    display: 'flex',
    flexDirection: 'column',
    width: '300px',
    gap: '18px',
  },
  title: {
    textAlign: 'center',
    marginBottom: '10px',
    color: '#333',
  },
  input: {
    padding: '12px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontSize: '14px',
    outline: 'none',
  },
  passwordWrapper: {
    position: 'relative',
  },
  eyeButton: {
    position: 'absolute',
    right: '10px',
    top: '50%',
    transform: 'translateY(-50%)',
    cursor: 'pointer',
    fontSize: '18px',
  },
  button: {
    padding: '12px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    fontWeight: 'bold',
    fontSize: '15px',
    cursor: 'pointer',
  },
};

export default LoginPage;
